"use strict";

var burgerBtn = document.querySelector(".jsBurgerBtn"),
    headerMenu = document.querySelector(".jsHederMenu"),
    body = document.querySelector(".body");
burgerBtn.addEventListener("click", function () {
  window.innerWidth <= 1024 && (headerMenu.classList.contains("mobile-menu--active") ? (body.style.overflow = "auto", headerMenu.classList.remove("mobile-menu--active"), burgerBtn.classList.remove("mobile-menu--active")) : (headerMenu.classList.add("mobile-menu--active"), burgerBtn.classList.add("mobile-menu--active"), body.style.overflow = "hidden"));
});